mapper.update:
update md
set md_stock = md_stock - #{buy_now_qty}
where md_id = #{buy_now_md_id}

update coupon
set coupon_yn = 'Y'
where coupon_id = #{coupon_id} and member_id = #{member_id};

mapper.select:
select coupon_id, member_id, coupon_name, coupon_dis, coupon_start, coupon_end, coupon_yn
from coupon
where member_id = #{member_id}

mapper.update:
update delivery
set used_date = now()
where delivery_id = #{delivery_id}
and member_id = #{member_id}

mapper.delete:
delete from cart c
where c.cart_id in

		#{cart_id}

mapper.insert:
insert into order_detail (order_id, md_id , order_detail_qty, order_detail_price, order_detail_amt , order_detail_discount , order_detail_discount_amt , order_detail_pay_amt) select #{order_id}, md.md_id , c.buy_qty, md.md_price, c.buy_qty * md.md_price , md.md_discount , c.buy_qty * md.md_price / 100 * md.md_discount , (c.buy_qty * md.md_price) - (c.buy_qty * md.md_price / 100 * md.md_discount) from cart c, md md where c.cart_id in
#{cart_id}
and c.md_id = md.md_id order by c.reg_date

insert into order_main (member_id,  delivery_id, order_md_cnt
						, order_amt, discount_amt, pay_amt, order_date, coupon_dis)
values (#{member_id},  #{delivery_id}, #{order_md_cnt}
		, #{order_amt}, #{discount_amt}, #{pay_amt}, now(), #{coupon_dis});

	SELECT LAST_INSERT_ID()
